package com.example.diplom2022.database

import android.content.Context
import androidx.annotation.WorkerThread
import androidx.room.Database
import androidx.room.Room
import androidx.room.RoomDatabase
import androidx.sqlite.db.SupportSQLiteDatabase
import com.example.diplom2022.database.dao.LessonDao
import com.example.diplom2022.database.dao.TestDao
import com.example.diplom2022.database.entities.Favorites
import com.example.diplom2022.database.entities.Lesson
import com.example.diplom2022.database.entities.Question
import com.example.diplom2022.database.entities.Test

@Database(
    entities = arrayOf(Question::class, Test::class, Lesson::class, Favorites::class),
    version = 1
)
abstract class AppRoomDatabase : RoomDatabase() {

    abstract fun lessonDao(): LessonDao
    abstract fun testDao(): TestDao

    companion object {

        @Volatile
        private var INSTANCE: AppRoomDatabase? = null

        fun getInstance(context: Context): AppRoomDatabase =
            INSTANCE ?: synchronized(this) {
                INSTANCE ?: buildDatabase(context).also { INSTANCE = it }
            }

        private fun buildDatabase(context: Context) =
            Room.databaseBuilder(
                context.applicationContext,
                AppRoomDatabase::class.java, "Sample.db"
            )
                // prepopulate the database after onCreate was called
                .addCallback(object : Callback() {
                    override fun onCreate(db: SupportSQLiteDatabase) {
                        super.onCreate(db)
                        // insert the data on the IO Thread
                        ioThread {
                            getInstance(context).lessonDao().insertLessons(PREPOPULATE_DATA_LESSONS)
                        }
                    }
                })
                .build()

        val PREPOPULATE_DATA_LESSONS = listOf(
            Lesson(
                0,
                "Урок 1. Введение в информационные технологии. Эволюция информационных технологий.",
